mapper.select:
select * from ( select id, supplier_core_ep_id, (select `name` from t_ep where id=supplier_core_ep_id ) as supplier_name , (select link_phone from t_ep where id=supplier_core_ep_id) as supplier_phone , seller_core_ep_id, (select `name` from t_ep where id =seller_core_ep_id) as seller_name , (select link_phone from t_ep where id =seller_core_ep_id) as seller_phone , truncate(rate/100,2) as rate from t_core_ep_channel ) f where 1=1
and f.`supplier_name` like CONCAT('%',#{supplier_name}'%')
limit #{record_start},#{record_count}
