mapper.select:
select tc.id,tc.supplier_core_ep_id,tc.seller_core_ep_id, truncate(rate/100,2) as rate, t1.name supplier_name,t1.link_phone supplier_phone, t2.`name` seller_name,t2.link_phone seller_phone from t_core_ep_channel tc left join t_ep t1 on tc.supplier_core_ep_id=t1.id left join t_ep t2 on tc.seller_core_ep_id =t2.id where 1=1
and t1.`name` like CONCAT('%',#{supplier_name},'%')
limit #{record_start},#{record_count}
