mapper.update:
update t_ep
set core_ep_id= #{id}
where id=#{id}

update t_ep
set status_bak=  status  ,`status`=#{status}
where  core_ep_id=#{id}

 update t_ep
set `status`=status_bak
where core_ep_id=#{id} and id!=#{id}

