mapper.select:
select credit_after from t_log_credit
 where  ep_id=#{ep_id} and  core_ep_id=#{core_ep_id}
ORDER BY credit_date desc limit 1

select t.id,name,linkman,link_phone,province,city,area,credit_after, DATE_FORMAT(credit_date,'%Y-%m-%d') as credit_date from ( select * from( select * from t_log_credit
where core_ep_id=#{core_ep_id}
ORDER BY credit_date desc ) as a GROUP BY ep_id) as credit RIGHT JOIN t_ep t on t.id = credit.ep_id where 1=1

select count(1) from ( select * from( select * from t_log_credit
where core_ep_id=#{core_ep_id}
ORDER BY credit_date desc ) as a GROUP BY ep_id) as credit RIGHT JOIN t_ep t on t.id = credit.ep_id where 1=1
