mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_sms_send (ep_id, send_phone1,send_phone2, threshold_status
  )
values (#{send_ep_id}, #{send_phone1}, #{send_phone2}, #{threshold_status}
  )

mapper.update:
update t_sms_send
set
  send_phone1 = #{send_phone1},
   send_phone2 = #{send_phone2},
  threshold_status = #{threshold_status}
where id = #{id}

mapper.sql:
select te.id as send_ep_id,ts.id,ts.send_phone1,ts.send_phone2,ts.threshold_status,te.name,te.ep_type , '销售商' as type_name,tet.threshold from t_ep te LEFT JOIN t_sms_send ts on(ts.ep_id=te.id) LEFT JOIN t_ep_balance_threshold tet on (te.id=tet.ep_id and tet.core_ep_id=te.core_ep_id) where te.core_ep_id=#{ep_id} and te.ep_type=10003
and name like #{name}
and threshold BETWEEN #{start_threshold} AND #{end_threshold}
