mapper.insert:

    SELECT LAST_INSERT_ID()

insert into t_capital_serial (id, ref_id, ref_type,
capital_id, old_balance, old_can_cash,
new_balance, new_can_cash, create_time
)
values (#{id,jdbcType=INTEGER}, #{refId,jdbcType=VARCHAR}, #{refType,jdbcType=INTEGER},
#{capitalId,jdbcType=INTEGER}, #{oldBalance,jdbcType=INTEGER}, #{oldCanCash,jdbcType=INTEGER},
#{newBalance,jdbcType=INTEGER}, #{newCanCash,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
)

mapper.select:
select
c.id,c.ref_id,c.ref_type, c.create_time as createTime,c.new_balance-c.old_balance as balanceAdd,c.new_can_cash-c.old_can_cash as
canCashAdd,p.name as changeType
from t_capital_serial c
LEFT JOIN t_payment_param p on c.ref_type=p.id
where capital_id = #{capitalId,jdbcType=INTEGER}

    limit #{startRecord}


    , #{maxRecords}
